import React, {useState} from 'react';
import Stock from './Stock';
import '../css/portfolio.css';
import {APIKEY, SANDBOXURL,VERSION} from '../constants'
function Portfolio() {
    const [ticker, setTicker] = useState("");
    const [quantity, setQuantity] = useState("");

    const handleQuantity = (e) => {
        setQuantity(e.target.value);
    }

    const handleTicker = (e) => {
        setTicker(e.target.value);
    }

    const handlePurchase = (e) => {
        e.preventDefault();
        getLatestPrice(ticker);
    }

    const fetchAllStocks = () => {
        fetch (SANDBOXURL + VERSION + `ref-data/iex/symbols?token=${APIKEY}`)
        .then(data => data.json())
        .then(stocks => {
            console.log(stocks);
            // stocks.forEach(stock => {
            //     getLatestPrice(stock.symbol)    
            // });
        }) 
    }

    const getLatestPrice = (stock) => {
                //https://sandbox.iexapis.com/stable/stock/twtr/price?token=Tsk_4ec0d0f4b3304d4090725944321b65b6
        fetch (SANDBOXURL + VERSION + `stock/${stock}/price?token=${APIKEY}`)
        .then(response => {
            return response.ok ? response.json() : Error(response.statusText);

        })
        .then(price => {
            console.log(price);
        }).catch(error=> {
            console.log(error);
        })
    }

    const renderOwnedStocks = () => {
        let testing = [];
        for (let i = 0; i < 10; ++i) {
            testing.push(<Stock/>);
        }
        return testing;
    }

    return (
        <header className="portfolioHeader">Portfolio ($5000.00)
            <div className="portfolioContainer">
                <div className="stocksContainer">
                    <div className="stocksList">
                        {renderOwnedStocks()}
                    </div>
                </div>
                <div className="portfolioDivider"></div>
                <div className="purchaseContainer">
                    <header id="wallet">Wallet</header>
                    <form className="purchaseForm" >
                        <input className="purchaseField" value={ticker} onChange={(event )=> handleTicker(event)} type="text" placeholder=" Ticker" />
                        <input className="purchaseField" value={quantity} onChange={event => handleQuantity(event)}type="text" placeholder=" Qty" />
                        <input className="purchaseButton" onClick={(event)=> handlePurchase(event)} type="submit" value="Buy"/>
                    </form>
                </div>
            </div>
        </header>
    );
}

export default Portfolio;
