import React from 'react';
import {useForm} from 'react-hook-form';
import '../css/register.css'
function Register() {
    const {register, handleSignup, errors} = useForm();

    const onSubmit = data => {
        console.log(data);
    }

    return (
        <div className="registerContainer">
            <form className="registerForm" onsubmit={handleSignup(onSubmit)}>
                <header id="registerHeader">Register</header>
                <input className="registerField" required type="text" placeholder="name" value ={name}/>
                <input className="registerField" required type="text" placeholder="email" value ={email}/>
                <input className="registerField" required type="password" placeholder="password" value ={password} onChange={(e)=> setPassword(e.target.value)}/>
                <input className="registerField" required type="password" placeholder="confirm password" value ={confirmPassword} onChange={(e)=> setConfirmPassword(e.target.value)}/>
                <input className="registerButton" type="submit" value="Sign UP"/>
            </form>     
        </div>
    );
}

// export default Register;


