        const interval = setInterval(() => {
            fetchStockPrices();
        }, 2000);
        return () => {
            clearInterval(interval);
          };
    }, []);
    const fetchStockPrices = () => {
        fetch(SANDBOXURL + VERSION + `stock/${props.ticker}/book?token=${APIKEY}`)
        .then(resp => {
            resp.json()
        })
