    }

    return (
        <div className="registerContainer">
            <form className="registerForm">
                <header id="registerHeader">Register</header>
                <input className="registerField" required type="text" placeholder="name" value ={name} onChange={(e)=> setName(e.target.value)}/>
                <input className="registerField" type="text" placeholder="email" value ={email} onChange={(e)=> setEmail(e.target.value)}/>
                <input className="registerField" type="password" placeholder="password" value ={password} onChange={(e)=> setPassword(e.target.value)}/>
                <input className="registerField" type="password" placeholder="confirm password" value ={confirmPassword} onChange={(e)=> setConfirmPassword(e.target.value)}/>
                <input className="registerButton" type="submit" value="Sign UP" onClick={(e) => handleSignup()}/>
            </form>     
        </div>
    );
}

export default Register;
