import React from 'react';
import '../css/stockPortfolio.css'

function Stock(props) {

    const convertNumToCurrencyFormat = (price) => {
        return price.toLocaleString('en-US', { style: 'currency', currency: 'USD' });
    }

    return (
        props ? (  
        <div className="stockPortfolio">
            <div className="stockCurrentPrice">({props.ticker.toUpperCase()}) - {props.quantity} share(s)    <span>{convertNumToCurrencyFormat(props.price)}</span></div>
            <div className="stockOpenPrice"> Open </div>
            <div className="stockLatestPrice"> </div>
        </div>
        )
        : 
        null
    );
}

export default Stock;
