import React, { useState, useEffect } from 'react';
import '../css/stockPortfolio.css';
import {SANDBOXURL, VERSION} from '../constants';

function StockPortfolio(props) {
    const [openPrice, setOpenPrice ] = useState(0);
    const [latestPrice, setLatestPrice ] = useState(4);
    useEffect(() => {
        const interval = setInterval(() => {
            setOpenPrice(openPrice+1);
            setLatestPrice(latestPrice+1);
            // fetchStockPrices();
        }, 1000);
    }, [openPrice, latestPrice]);


    const convertNumToCurrencyFormat = (price) => {
        return price.toLocaleString('en-US', { style: 'currency', currency: 'USD' });
    }

    return (
        props ? (  
        <div className="stockPortfolio">
            <div>({props.ticker.toUpperCase()}) - {props.quantity} share(s)    <span className="stockCurrentPrice">{convertNumToCurrencyFormat(props.price)}</span></div>
            <div className="stockOpenPrice">open price:       {convertNumToCurrencyFormat(openPrice)} </div>
            <div className="stockLatestPrice">latest price:     {convertNumToCurrencyFormat(latestPrice)}</div>
        </div>
        )
        : 
        null
    );
}

export default StockPortfolio;
