import React from 'react';
import {useForm} from 'react-hook-form';
import '../css/register.css'

function Register() {
  const { register, handleSubmit, errors } = useForm(); // initialise the hook
  const onSubmit = data => {
    console.log(data);
  };

  return (
      <div className="registerContainer">
        <form className="registerForm" onSubmit={handleSubmit(onSubmit)}>
            <header id="registerHeader">Register</header>
            <input className="registerField" name="name" ref={register({ required: true})}/>
            {errors.lastname && 'Last name is required.'}

            <input className="registerField" name="email" ref={register({ required: true })} />
            {errors.lastname && 'Last name is required.'}

            <input className="registerField" type="password" name="password" ref={register({ required:true })} />
            {errors.age && 'Please enter number for age.'}

            <input className="registerField" type="password" name="confirmPassword" ref={register({ required: true })} />
            {errors.lastname && 'Last name is required.'}

            <input className="registerButton" type="submit" placeholder />
        </form>
    </div>
  );
}

export default Register;


