import React, { useState } from 'react';
import Stock from './Stock';
import NavBar from './NavBar';
import '../css/portfolio.css';
import { APIKEY, SANDBOXURL,VERSION } from '../constants'

function Portfolio(props) {
    const [ticker, setTicker] = useState("");               // hook, ticker of stock
    const [quantity, setQuantity] = useState("");           // hook, amount of stocks
    const [price, setPrice] = useState("");                 // hook, price of stock
    // hooks to display error messages
    const [validTicker, setValidTicker] = useState(true);   
    const [validCash, setValidCash] = useState(true);


    const handlePurchase = (e) => {
        e.preventDefault();
        getLatestPrice(ticker);
    }

    const resetErrorMessages = () => {      // remove error messages after displaying to user
        setValidTicker(true);
        setValidCash(true);
    }

    const getLatestPrice = (stock) => {
        fetch (SANDBOXURL + VERSION + `stock/${stock}/price?token=${APIKEY}`)
        .then(response => {
            if (response.ok) {
                return response.json();
            }
            throw response;
        })
        .then(stockPrice => {
            setPrice(stockPrice);
            if ((stockPrice * quantity) > props.user.cash) {
                setValidCash(false);
            }
            else {      
                buyStock();
            }                
            console.log(price);
        })
        .catch(error => {
            setValidTicker(false);
        })
    }
    const buyStock = () => {
        fetch()    
    }

    const renderOwnedStocks = () => {
        let testing = [];
        for (let i = 0; i < 10; ++i) {
            testing.push(<Stock/>);
        }
        return testing;
    }

    const convertNumToCurrencyFormat = (price) => {
        return price.toLocaleString('en-US', { style: 'currency', currency: 'USD' });
    }

    return (
        <div>
            { Object.keys(props.user).length !== 0 && (         // check if user is loggedIn
                <div>
                    <NavBar/>                
                    <header className="portfolioHeader"> Portfolio (${}) </header>
                    <div className="portfolioContainer">
                        <div className="stocksContainer">
                            <div className="stocksList">
                                {renderOwnedStocks()}
                            </div>
                        </div>
                        <div className="portfolioDivider"></div>
                        <div className="purchaseContainer">
                            <header id="wallet">Cash - {convertNumToCurrencyFormat(props.user.cash)}</header>
                            <form className="purchaseForm" onClick={()=>resetErrorMessages()}>
                                <input className="purchaseField" type="text" placeholder="Ticker" value={ticker} onChange={e => setTicker(e.target.value)} />
                                <input className="purchaseField" type="text" placeholder="Qty" value={quantity} onChange={e => setQuantity(e.target.value)} />
                                {!validTicker && <p className="purchaseErrorMessage">"{ticker}" not found</p>}
                                {!validCash && <p className="purchaseErrorMessage">not enough cash, {quantity} stock(s) of {ticker} cost {convertNumToCurrencyFormat(price*quantity)}</p>}
                                <input className="purchaseButton" type="submit" value="Buy" onClick={e => handlePurchase(e)}/>
                            </form>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default Portfolio;
