import React, {useState} from 'react';
import Stock from './Stock';
import '../css/portfolio.css';
import {APIKEY, SANDBOXURL,VERSION} from '../constants'

function Portfolio() {
    const [ticker, setTicker] = useState("");
    const [quantity, setQuantity] = useState("");

    const handlePurchase = (e) => {
        e.preventDefault();
        getLatestPrice(ticker);
    }

    const fetchAllStocks = () => {
        fetch (SANDBOXURL + VERSION + `ref-data/iex/symbols?token=${APIKEY}`)
        .then(data => data.json())
        .then(stocks => {
            console.log(stocks);
        }) 
    }

    const getLatestPrice = (stock) => {
        fetch (SANDBOXURL + VERSION + `stock/${stock}/price?token=${APIKEY}`)
        .then(response => {
            if (response.ok) {
                return response.json();
            }
            throw response;
        })
        .then(price => {
            console.log(price);
        })
        .catch(error => {
            // do something with the error
        })
    }


    const renderOwnedStocks = () => {
        let testing = [];
        for (let i = 0; i < 10; ++i) {
            testing.push(<Stock/>);
        }
        return testing;
    }

    return (
        <header className="portfolioHeader">Portfolio ($5000.00)
            <div className="portfolioContainer">
                <div className="stocksContainer">
                    <div className="stocksList">
                        {renderOwnedStocks()}
                    </div>
                </div>
                <div className="portfolioDivider"></div>
                <div className="purchaseContainer">
                    <header id="wallet">Wallet</header>
                    <form className="purchaseForm" >
                        <input className="purchaseField" value={ticker} type="text" placeholder=" Ticker" onChange={e => setTicker(e.target.value)} />
                        <input className="purchaseField" value={quantity} type="text" placeholder=" Qty" onChange={e => setQuantity(e.target.value)} />
                        <input className="purchaseButton"  type="submit"  onClick={e => handlePurchase(e)}/>
                    </form>
                </div>
            </div>
        </header>
    );
}

export default Portfolio;
